r content server.\\r\\nbackend default {\\r\\n.host = \\\"127.0.0.1\\\"; # UPDATE this only if the web server is not on the same machine\\r\\n.port = \\\"8080\\\";      # UPDATE 8080 with your web server\'s (internal) port\\r\\n}\\r\\nsub vcl_recv {\\r\\n/*\\r\\n# Blocks\\r\\nif (req.http.user-agent ~ \\\"^$\\\" \\u0026\\u0026 req.http.referer ~ \\\"^$\\\") {\\r\\nreturn (synth(204, \\\"No content\\\"));\\r\\n}\\r\\nif (req.http.user-agent ~ \\\"(ahrefs|bingbot|domaincrawler|dotbot|mj12bot|semrush)\\\") {\\r\\nreturn (synth(204, \\\"Bot blocked\\\"));\\r\\n}\\r\\n# If we host multiple domains on a server, here you can list the domains you DO NOT want to cache\\r\\n# The first check matches both naked \\u0026 \\\"www\\\" subdomains. Use the second for non generic subdomains.\\r\\nif (\\r\\nreq.http.host ~ \\\"(www.)?(domain1.com|domain2.org|domain3.net)\\\" ||\\r\\nreq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\"\\r\\n) {\\r\\nreturn (pass);\\r\\n}\\r\\n*/\\r\\n# LetsEncrypt Certbot passthrough\\r\\nif (req.url ~ \\\"^/.well-known/acme-challenge/\\\") {\\r\\nreturn (pass);\\r\\n}\\r\\n# Forward client\'s IP to the backend\\r\\nif (req.restarts == 0) {\\r\\nif (req.http.X-Real-IP) {\\r\\nset req.http.X-Forwarded-For = req.http.X-Real-IP;\\r\\n} else if (req.http.X-Forwarded-For) {\\r\\nset req.http.X-Forwarded-For = req.http.X-Forwarded-For + \\\", \\\" + client.ip;\\r\\n} else {\\r\\nset req.http.X-Forwarded-For = client.ip;\\r\\n}\\r\\n}\\r\\n# httpoxy\\r\\nunset req.http.proxy;\\r\\n# Non-RFC2616 or CONNECT which is weird.\\r\\nif (\\r\\nreq.method != \\\"GET\\\" \\u0026\\u0026\\r\\nreq.method != \\\"HEAD\\\" \\u0026\\u0026\\r\\nreq.method != \\\"PUT\\\" \\u0026\\u0026\\r\\nreq.method != \\\"POST\\\" \\u0026\\u0026\\r\\nreq.method != \\\"TRACE\\\" \\u0026\\u0026\\r\\nreq.method != \\\"OPTIONS\\\" \\u0026\\u0026\\r\\nreq.method != \\\"DELETE\\\"\\r\\n) {\\r\\nreturn (pipe);\\r\\n}\\r\\n# We only deal with GET and HEAD by default\\r\\nif (req.method != \\\"GET\\\" \\u0026\\u0026 req.method != \\\"HEAD\\\") {\\r\\nreturn (pass);\\r\\n}\\r\\n# === URL manipulation ===\\r\\n# First remove the Google Analytics added parameters, useless for our backend\\r\\nif (req.url ~ \\\"(?|\\u0026)(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=\\\") {\\r\\nset req.url = regsuball(req.url, \\\"\\u0026(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=([A-z0-9_-.%25]+)\\\", \\\"\\\");\\r\\nset req.url = regsuball(req.url, \\\"?(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=([A-z0-9_-.%25]+)\\\", \\\"?\\\");\\r\\nset req.url = regsub(req.url, \\\"?\\u0026\\\", \\\"?\\\");\\r\\nset req.url = regsub(req.url, \\\"?$\\\", \\\"\\\");\\r\\n}\\r\\n# Strip hash, server doesn\'t need it.\\r\\nif (req.url ~ \\\"#\\\") {\\r\\nset req.url = regsub(req.url, \\\"#.*$\\\", \\\"\\\");\\r\\n}\\r\\n# Strip a trailing ? if it exists\\r\\n#if (req.url ~ \\\"?$\\\") {\\r\\n#    set req.url = regsub(req.url, \\\"?$\\\", \\\"\\\");\\r\\n#}\\r\\n# === Generic cookie manipulation ===\\r\\n# Remove the \\\"has_js\\\" cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"has_js=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove any Google Analytics based cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__utm.=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"_ga=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"_gat=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmctr=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmcmd.=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmccn.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove DoubleClick offensive cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__gads=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the Quant Capital cookies (added by some plugin, all __qca)\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__qc.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the AddThis cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__atuv.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the wp-settings-1 cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"wp-settings-1=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the wp-settings-time-1 cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"wp-settings-time-1=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the wp test cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"wordpress_test_cookie=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove a \\\";\\\" prefix in the cookie if present\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"^;s*\\\", \\\"\\\");\\r\\n# Are there cookies left with only spaces or that are empty?\\r\\nif (req.http.cookie ~ \\\"^s*$\\\") {\\r\\nunset req.http.cookie;\\r\\n}\\r\\n# Check for the custom \\\"X-Logged-In\\\" header (used by K2 and other apps) to identify\\r\\n# if the visitor is a guest, then unset any cookie (including session cookies) provided\\r\\n# it\'s not a POST request.\\r\\nif(req.http.X-Logged-In == \\\"False\\\" \\u0026\\u0026 req.method != \\\"POST\\\") {\\r\\nunset req.http.Cookie;\\r\\n}\\r\\n# === DO NOT CACHE ===\\r\\n# Don\'t cache HTTP authorization/authentication pages and pages with certain headers or cookies\\r\\nif (\\r\\nreq.http.Authorization ||\\r\\nreq.http.Authenticate ||\\r\\nreq.http.X-Logged-In == \\\"True\\\" ||\\r\\nreq.http.Cookie ~ \\\"userID\\\" ||\\r\\nreq.http.Cookie ~ \\\"joomla_[a-zA-Z0-9_]+\\\" ||\\r\\nreq.http.Cookie ~ \\\"(wordpress_[a-zA-Z0-9_]+|wp-postpass|comment_author_[a-zA-Z0-9_]+|woocommerce_cart_hash|woocommerce_items_in_cart|wp_woocommerce_session_[a-zA-Z0-9]+)\\\"\\r\\n) {\\r\\n#set req.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set req.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set req.http.Pragma = \\\"no-cache\\\";\\r\\nreturn (pass);\\r\\n}\\r\\n# Exclude the following paths (e.g. backend admins, user pages or ad URLs that require tracking)\\r\\n# In Joomla specifically, you are advised to create specific entry points (URLs) for users to\\r\\n# interact with the site (either common user logins or even commenting), e.g. make a menu item\\r\\n# to point to a user login page (e.g. /login), including all related functionality such as\\r\\n# password reset, email reminder and so on.\\r\\nif(\\r\\nreq.url ~ \\\"^/addons\\\" ||\\r\\nreq.url ~ \\\"^/administrator\\\" ||\\r\\nreq.url ~ \\\"^/cart\\\" ||\\r\\nreq.url ~ \\\"^/checkout\\\" ||\\r\\nreq.url ~ \\\"^/component/banners\\\" ||\\r\\nreq.url ~ \\\"^/component/socialconnect\\\" ||\\r\\nreq.url ~ \\\"^/component/users\\\" ||\\r\\nreq.url ~ \\\"^/connect\\\" ||\\r\\nreq.url ~ \\\"^/contact\\\" ||\\r\\nreq.url ~ \\\"^/login\\\" ||\\r\\nreq.url ~ \\\"^/logout\\\" ||\\r\\nreq.url ~ \\\"^/lost-password\\\" ||\\r\\nreq.url ~ \\\"^/my-account\\\" ||\\r\\nreq.url ~ \\\"^/register\\\" ||\\r\\nreq.url ~ \\\"^/signin\\\" ||\\r\\nreq.url ~ \\\"^/signup\\\" ||\\r\\nreq.url ~ \\\"^/wc-api\\\" ||\\r\\nreq.url ~ \\\"^/wp-admin\\\" ||\\r\\nreq.url ~ \\\"^/wp-login.php\\\" ||\\r\\nreq.url ~ \\\"^?add-to-cart=\\\" ||\\r\\nreq.url ~ \\\"^?wc-api=\\\"\\r\\n) {\\r\\n#set req.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set req.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set req.http.Pragma = \\\"no-cache\\\";\\r\\nreturn (pass);\\r\\n}\\r\\n# Don\'t cache ajax requests\\r\\nif(req.http.X-Requested-With == \\\"XMLHttpRequest\\\" || req.url ~ \\\"nocache\\\") {\\r\\n#set req.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set req.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set req.http.Pragma = \\\"no-cache\\\";\\r\\nreturn (pass);\\r\\n}\\r\\n# === STATIC FILES ===\\r\\n# Properly handle different encoding types\\r\\nif (req.http.Accept-Encoding) {\\r\\nif (req.url ~ \\\".(jpg|jpeg|png|gif|gz|tgz|bz2|tbz|mp3|ogg|swf)$\\\") {\\r\\n# No point in compressing these\\r\\nunset req.http.Accept-Encoding;\\r\\n} elseif (req.http.Accept-Encoding ~ \\\"gzip\\\") {\\r\\nset req.http.Accept-Encoding = \\\"gzip\\\";\\r\\n} elseif (req.http.Accept-Encoding ~ \\\"deflate\\\") {\\r\\nset req.http.Accept-Encoding = \\\"deflate\\\";\\r\\n} else {\\r\\n# unknown algorithm (aka crappy browser)\\r\\nunset req.http.Accept-Encoding;\\r\\n}\\r\\n}\\r\\n# Remove all cookies for static files \\u0026 deliver directly\\r\\nif (req.url ~ \\\"^[^?]*.(7z|avi|bmp|bz2|css|csv|doc|docx|eot|flac|flv|gif|gz|ico|jpeg|jpg|js|less|mka|mkv|mov|mp3|mp4|mpeg|mpg|odt|ogg|ogm|opus|otf|pdf|png|ppt|pptx|rar|rtf|svg|svgz|swf|tar|tbz|tgz|ttf|txt|txz|wav|webm|webp|woff|woff2|xls|xlsx|xml|xz|zip)(?.*)?$\\\") {\\r\\nunset req.http.Cookie;\\r\\nreturn (hash);\\r\\n}\\r\\nreturn (hash);\\r\\n}\\r\\nsub vcl_backend_response {\\r\\n/*\\r\\n# If we host multiple domains on a server, here you can list the domains you DO NOT want to cache\\r\\n# The first check matches both naked \\u0026 \\\"www\\\" subdomains. Use the second for non generic subdomains.\\r\\nif (\\r\\nbereq.http.host ~ \\\"(www.)?(domain1.com|domain2.org|domain3.net)\\\" ||\\r\\nbereq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\"\\r\\n) {\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n*/\\r\\n# Don\'t cache 50x responses\\r\\nif (\\r\\nberesp.status == 500 ||\\r\\nberesp.status == 502 ||\\r\\nberesp.status == 503 ||\\r\\nberesp.status == 504\\r\\n) {\\r\\nreturn (abandon);\\r\\n}\\r\\n# === DO NOT CACHE ===\\r\\n# Exclude the following paths (e.g. backend admins, user pages or ad URLs that require tracking)\\r\\n# In Joomla specifically, you are advised to create specific entry points (URLs) for users to\\r\\n# interact with the site (either common user logins or even commenting), e.g. make a menu item\\r\\n# to point to a user login page (e.g. /login), including all related functionality such as\\r\\n# password reset, email reminder and so on.\\r\\nif(\\r\\nbereq.url ~ \\\"^/addons\\\" ||\\r\\nbereq.url ~ \\\"^/administrator\\\" ||\\r\\nbereq.url ~ \\\"^/cart\\\" ||\\r\\nbereq.url ~ \\\"^/checkout\\\" ||\\r\\nbereq.url ~ \\\"^/component/banners\\\" ||\\r\\nbereq.url ~ \\\"^/component/socialconnect\\\" ||\\r\\nbereq.url ~ \\\"^/component/users\\\" ||\\r\\nbereq.url ~ \\\"^/connect\\\" ||\\r\\nbereq.url ~ \\\"^/contact\\\" ||\\r\\nbereq.url ~ \\\"^/login\\\" ||\\r\\nbereq.url ~ \\\"^/logout\\\" ||\\r\\nbereq.url ~ \\\"^/lost-password\\\" ||\\r\\nbereq.url ~ \\\"^/my-account\\\" ||\\r\\nbereq.url ~ \\\"^/register\\\" ||\\r\\nbereq.url ~ \\\"^/signin\\\" ||\\r\\nbereq.url ~ \\\"^/signup\\\" ||\\r\\nbereq.url ~ \\\"^/wc-api\\\" ||\\r\\nbereq.url ~ \\\"^/wp-admin\\\" ||\\r\\nbereq.url ~ \\\"^/wp-login.php\\\" ||\\r\\nbereq.url ~ \\\"^?add-to-cart=\\\" ||\\r\\nbereq.url ~ \\\"^?wc-api=\\\"\\r\\n) {\\r\\n#set beresp.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set beresp.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set beresp.http.Pragma = \\\"no-cache\\\";\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Don\'t cache HTTP authorization/authentication pages and pages with certain headers or cookies\\r\\nif (\\r\\nbereq.http.Authorization ||\\r\\nbereq.http.Authenticate ||\\r\\nbereq.http.X-Logged-In == \\\"True\\\" ||\\r\\nbereq.http.Cookie ~ \\\"userID\\\" ||\\r\\nbereq.http.Cookie ~ \\\"joomla_[a-zA-Z0-9_]+\\\" ||\\r\\nbereq.http.Cookie ~ \\\"(wordpress_[a-zA-Z0-9_]+|wp-postpass|comment_author_[a-zA-Z0-9_]+|woocommerce_cart_hash|woocommerce_items_in_cart|wp_woocommerce_session_[a-zA-Z0-9]+)\\\"\\r\\n) {\\r\\n#set beresp.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set beresp.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set beresp.http.Pragma = \\\"no-cache\\\";\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Don\'t cache ajax requests\\r\\nif(beresp.http.X-Requested-With == \\\"XMLHttpRequest\\\" || bereq.url ~ \\\"nocache\\\") {\\r\\n#set beresp.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set beresp.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set beresp.http.Pragma = \\\"no-cache\\\";\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Don\'t cache backend response to posted requests\\r\\nif (bereq.method == \\\"POST\\\") {\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Ok, we\'re cool \\u0026 ready to cache things\\r\\n# so let\'s clean up some headers and cookies\\r\\n# to maximize caching.\\r\\n# Check for the custom \\\"X-Logged-In\\\" header to identify if the visitor is a guest,\\r\\n# then unset any cookie (including session cookies) provided it\'s not a POST request.\\r\\nif(beresp.http.X-Logged-In == \\\"False\\\" \\u0026\\u0026 bereq.method != \\\"POST\\\") {\\r\\nunset beresp.http.Set-Cookie;\\r\\n}\\r\\n# Unset the \\\"pragma\\\" header (suggested)\\r\\nunset beresp.http.Pragma;\\r\\n# Unset the \\\"vary\\\" header (suggested)\\r\\nunset beresp.http.Vary;\\r\\n# Unset the \\\"etag\\\" header (optional)\\r\\n#unset beresp.http.etag;\\r\\n# Allow stale content, in case the backend goes down\\r\\nset beresp.grace = 24h;\\r\\n# Enforce your own cache TTL (optional)\\r\\n#set beresp.ttl = 180s;\\r\\n# Modify \\\"expires\\\" header - https://www.varnish-cache.org/trac/wiki/VCLExampleSetExpires (optional)\\r\\n#set beresp.http.Expires = \\\"\\\" + (now + beresp.ttl);\\r\\n# If your backend server does not set the right caching headers for static assets,\\r\\n# you can set them below (uncomment first and change 604800 - which 1 week - to whatever you\\r\\n# want (in seconds)\\r\\n#if (bereq.url ~ \\\".(ico|jpg|jpeg|gif|png|bmp|webp|tiff|svg|svgz|pdf|mp3|flac|ogg|mid|midi|wav|mp4|webm|mkv|ogv|wmv|eot|otf|woff|ttf|rss|atom|zip|7z|tgz|gz|rar|bz2|tar|exe|doc|docx|xls|xlsx|ppt|pptx|rtf|odt|ods|odp)(?[a-zA-Z0-9=]+)$\\\") {\\r\\n#    set beresp.http.Cache-Control = \\\"public, max-age=604800\\\";\\r\\n#}\\r\\nif (bereq.url ~ \\\"^[^?]*.(7z|avi|bmp|bz2|css|csv|doc|docx|eot|flac|flv|gif|gz|ico|jpeg|jpg|js|less|mka|mkv|mov|mp3|mp4|mpeg|mpg|odt|ogg|ogm|opus|otf|pdf|png|ppt|pptx|rar|rtf|svg|svgz|swf|tar|tbz|tgz|ttf|txt|txz|wav|webm|webp|woff|woff2|xls|xlsx|xml|xz|zip)(?.*)?$\\\") {\\r\\nunset beresp.http.set-cookie;\\r\\nset beresp.do_stream = true;\\r\\n}\\r\\n# We have content to cache, but it\'s got no-cache or other Cache-Control values sent\\r\\n# So let\'s reset it to our main caching time (180s as used in this example configuration)\\r\\n# The additional parameters specified (stale-while-revalidate \\u0026 stale-if-error) are used\\r\\n# by modern browsers to better control caching. Set these to twice \\u0026 four times your main\\r\\n# cache time respectively.\\r\\n# This final setting will normalize cache-control headers for CMSs like Joomla\\r\\n# which set max-age=0 even when the CMS\' cache is enabled.\\r\\nif (beresp.http.Cache-Control !~ \\\"max-age\\\" || beresp.http.Cache-Control ~ \\\"max-age=0\\\") {\\r\\nset beresp.http.Cache-Control = \\\"public, max-age=180, stale-while-revalidate=360, stale-if-error=43200\\\";\\r\\n}\\r\\n# Optionally set a larger TTL for pages with less than 180s of cache TTL\\r\\n#if (beresp.ttl \\u003c 180s) {\\r\\n#    set beresp.http.Cache-Control = \\\"public, max-age=180, stale-while-revalidate=360, stale-if-error=43200\\\";\\r\\n#}\\r\\nreturn (deliver);\\r\\n}\\r\\nsub vcl_deliver {\\r\\n/*\\r\\n# Send a special header for excluded domains only\\r\\n# The if statement can be identical to the ones in the vcl_recv() and vcl_fetch() functions above\\r\\nif (\\r\\nreq.http.host ~ \\\"(www.)?(domain1.com|domain2.org|domain3.net)\\\" ||\\r\\nreq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\"\\r\\n) {\\r\\nset resp.http.X-Domain-Status = \\\"EXCLUDED\\\";\\r\\n}\\r\\n# Enforce redirect to HTTPS for specified domains only\\r\\nif (\\r\\nreq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\" \\u0026\\u0026\\r\\nreq.http.X-Forwarded-Proto !~ \\\"(?i)https\\\"\\r\\n) {\\r\\nset resp.http.Location = \\\"https://\\\" + req.http.host + req.url;\\r\\nset resp.status = 302;\\r\\n}\\r\\n*/\\r\\n# Send special headers that indicate the cache status of each web page\\r\\nif (obj.hits \\u003e 0) {\\r\\nset resp.http.X-Cache = \\\"HIT\\\";\\r\\nset resp.http.X-Cache-Hits = obj.hits;\\r\\n} else {\\r\\nset resp.http.X-Cache = \\\"MISS\\\";\\r\\n}\\r\\nreturn (deliver);\\r\\n}\\r\\nNginx Host 配置\\r\\nserver {\\r\\nlisten 80;\\r\\nserver_name www.phpman.cc phpman.cc;\\r\\nrewrite ^/(.*) https://www.phpman.cc/$1 permanent;\\r\\n}\\r\\nserver\\r\\n{\\r\\nlisten 8080;\\r\\nindex index.html index.htm index.php default.html default.htm default.php;\\r\\nroot /home/wwwroot/www.phpman.cc/wordpress;\\r\\ninclude rewrite/wordpress.conf;\\r\\n#error_page   404   /404.html;\\r\\n# Deny access to PHP files in specific directory\\r\\n#location ~ /(wp-content|uploads|wp-includes|images)/.*.php$ { deny all; }\\r\\ninclude enable-php-pathinfo.conf;\\r\\nlocation ~ .*.(gif|jpg|jpeg|png|bmp|swf)$\\r\\n{\\r\\nexpires      30d;\\r\\n}\\r\\nlocation ~ .*.(js|css)?$\\r\\n{\\r\\nexpires      12h;\\r\\n}\\r\\nlocation ~ /.well-known {\\r\\nallow all;\\r\\n}\\r\\nlocation ~ /.\\r\\n{\\r\\ndeny all;\\r\\n}\\r\\naccess_log  /home/wwwlogs/y.log;\\r\\n}\\r\\nserver\\r\\n{\\r\\nlisten 443 ssl http2;\\r\\n#listen [::]:443 ssl http2;\\r\\nserver_name www.phpman.cc phpman.cc;\\r\\nssl_certificate /root/ssl/4082867_phpman.cc.pem;\\r\\nssl_certificate_key /root/ssl/4082867_phpman.cc.key;\\r\\nssl_session_timeout 5m;\\r\\nssl_protocols TLSv1 TLSv1.1 TLSv1.2 TLSv1.3;\\r\\nssl_prefer_server_ciphers on;\\r\\nssl_ciphers \\\"TLS13-AES-256-GCM-SHA384:TLS13-CHACHA20-POLY1305-SHA256:TLS13-AES-128-GCM-SHA256:TLS13-AES-128-CCM-8-SHA256:TLS13-AES-128-CCM-SHA256:EECDH+CHACHA20:EECDH+CHACHA20-draft:EECDH+AES128:RSA+AES128:EECDH+AES256:RSA+AES256:EECDH+3DES:RSA+3DES:!MD5\\\";\\r\\nssl_session_cache builtin:1000 shared:SSL:10m;\\r\\n# openssl dhparam -out /usr/local/nginx/conf/ssl/dhparam.pem 2048\\r\\nssl_dhparam /usr/local/nginx/conf/ssl/dhparam.pem;\\r\\nlocation / {\\r\\nproxy_pass http://127.0.0.1:6081;\\r\\nproxy_set_header X-Real-IP  $remote_addr;\\r\\nproxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;\\r\\nproxy_set_header X-Forwarded-Proto https;\\r\\nproxy_set_header X-Forwarded-Port 443;\\r\\nproxy_set_header Host $host;\\r\\n}\\r\\n}\\r\\nWordPress wp-config.php配置\\r\\n/* SSL Settings */\\r\\ndefine(\'FORCE_SSL_ADMIN\', true);\\r\\n/* Turn HTTPS \'on\' if HTTP_X_FORWARDED_PROTO matches \'https\' */\\r\\nif (strpos($_SERVER[\'HTTP_X_FORWARDED_PROTO\'], \'https\') !== false) {\\r\\n$_SERVER[\'HTTPS\'] = \'on\';\\r\\n}\\r\\n重启Nginx 和 Varnish 服务\\r\\nservice nginx reload\\r\\nservice varnish reload\\r\\n总结\\r\\nWeb URL请求路径：\\r\\nNginx:80——\\u003eNginx:443 ——\\u003e Varnish:6081 ——\\u003e Nginx:8080\\r\\n验证Varnish是否生效\\r\\ncurl   -I https://www.phpman.cc\\r\\n返回结果\\r\\nHTTP/1.1 200 OK\\r\\nServer: nginx\\r\\nDate: Tue, 13 Apr 2021 06:41:21 GMT\\r\\nContent-Type: text/html; charset=UTF-8\\r\\nConnection: keep-alive\\r\\nVary: Accept-Encoding\\r\\nVary: Accept-Encoding\\r\\nX-Powered-By: PHP/7.4.15\\r\\nLink: \\u003chttps://www.phpman.cc/wp-json/\\u003e; rel=\\\"https://api.w.org/\\\"\\r\\nX-Varnish: 360465\\r\\nAge: 0\\r\\nVia: 1.1 varnish-v4\\r\\n\"],\"id\":[\"16\"],\"title\":[\"Varnish 加速你的WordPress\"]}','2021-09-12 14:37:11','2021-09-12 14:37:11'),(427,1,'/admin/info/posts','GET','::1','','2021-09-12 14:39:11','2021-09-12 14:39:11'),(428,1,'/admin/info/posts','GET','::1','','2021-09-12 14:40:51','2021-09-12 14:40:51'),(429,1,'/admin/info/posts/edit','GET','::1','','2021-09-12 14:40:56','2021-09-12 14:40:56'),(430,1,'/admin/info/posts/edit','GET','::1','','2021-09-12 14:41:20','2021-09-12 14:41:20'),(431,1,'/admin/info/posts/new','GET','::1','','2021-09-12 14:41:24','2021-09-12 14:41:24'),(432,1,'/admin/info/posts/new','GET','::1','','2021-09-12 14:41:49','2021-09-12 14:41:49'),(433,1,'/admin/info/posts/new','GET','::1','','2021-09-12 14:41:55','2021-09-12 14:41:55'),(434,1,'/admin/info/posts/new','GET','::1','','2021-09-12 14:43:34','2021-09-12 14:43:34'),(435,1,'/admin/info/posts/new','GET','::1','','2021-09-12 14:43:43','2021-09-12 14:43:43'),(436,1,'/admin/info/posts/new','GET','::1','','2021-09-12 14:43:46','2021-09-12 14:43:46'),(437,1,'/admin/info/posts/new','GET','::1','','2021-09-12 14:43:47','2021-09-12 14:43:47'),(438,1,'/admin/info/posts','GET','::1','','2021-09-12 14:43:51','2021-09-12 14:43:51'),(439,1,'/admin/info/posts/edit','GET','::1','','2021-09-12 14:43:52','2021-09-12 14:43:52'),(440,1,'/admin/edit/posts','POST','::1','{\"__go_admin_previous_\":[\"/admin/info/posts?__page=1\\u0026__pageSize=10\\u0026__sort=id\\u0026__sort_type=desc\"],\"__go_admin_t_\":[\"8e83f84d-dbfa-41ac-af71-521f71511d80\"],\"content\":[\"\\r\\nVarnish Cache是​​Web应用程序加速器，也称为缓存HTTP反向代理。您可以将其安装在任何使用HTTP的服务器之前，并将其配置为缓存内容。Varnish Cache确实非常快。根据您的体系结构，通常可以将交付速度提高300到1000倍。\\r\\nCentOs 安装\\r\\nyum install varnish\\r\\nVarnish 配置\\r\\n###############################################################################################\\r\\n### The perfect Varnish 4.x+ configuration for Joomla, WordPress \\u0026 other CMS based websites ###\\r\\n###############################################################################################\\r\\n######################\\r\\n#\\r\\n# UPDATED on July 7th, 2020\\r\\n#\\r\\n# Configuration Notes:\\r\\n# 1. Default dynamic content caching respects your backend\'s cache-control HTTP header.\\r\\n#    If however you need to enforce a different cache-control TTL,\\r\\n#    do a search for \\\"180\\\" and replace with the new value in seconds.\\r\\n#    Stale cache is served for up to 24 hours.\\r\\n# 2. Make sure you update the \\\"backend default { ... }\\\" section with the correct IP and port\\r\\n#\\r\\n######################\\r\\n# Varnish Reference:\\r\\n# See the VCL chapters in the Users Guide at https://www.varnish-cache.org/docs/\\r\\n# and https://www.varnish-cache.org/trac/wiki/VCLExamples for more examples.\\r\\n# Marker to tell the VCL compiler that this VCL has been adapted to the new 4.0 format\\r\\nvcl 4.0;\\r\\n# Imports\\r\\nimport std;\\r\\n# Default backend definition. Set this to point to your content server.\\r\\nbackend default {\\r\\n.host = \\\"127.0.0.1\\\"; # UPDATE this only if the web server is not on the same machine\\r\\n.port = \\\"8080\\\";      # UPDATE 8080 with your web server\'s (internal) port\\r\\n}\\r\\nsub vcl_recv {\\r\\n/*\\r\\n# Blocks\\r\\nif (req.http.user-agent ~ \\\"^$\\\" \\u0026\\u0026 req.http.referer ~ \\\"^$\\\") {\\r\\nreturn (synth(204, \\\"No content\\\"));\\r\\n}\\r\\nif (req.http.user-agent ~ \\\"(ahrefs|bingbot|domaincrawler|dotbot|mj12bot|semrush)\\\") {\\r\\nreturn (synth(204, \\\"Bot blocked\\\"));\\r\\n}\\r\\n# If we host multiple domains on a server, here you can list the domains you DO NOT want to cache\\r\\n# The first check matches both naked \\u0026 \\\"www\\\" subdomains. Use the second for non generic subdomains.\\r\\nif (\\r\\nreq.http.host ~ \\\"(www.)?(domain1.com|domain2.org|domain3.net)\\\" ||\\r\\nreq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\"\\r\\n) {\\r\\nreturn (pass);\\r\\n}\\r\\n*/\\r\\n# LetsEncrypt Certbot passthrough\\r\\nif (req.url ~ \\\"^/.well-known/acme-challenge/\\\") {\\r\\nreturn (pass);\\r\\n}\\r\\n# Forward client\'s IP to the backend\\r\\nif (req.restarts == 0) {\\r\\nif (req.http.X-Real-IP) {\\r\\nset req.http.X-Forwarded-For = req.http.X-Real-IP;\\r\\n} else if (req.http.X-Forwarded-For) {\\r\\nset req.http.X-Forwarded-For = req.http.X-Forwarded-For + \\\", \\\" + client.ip;\\r\\n} else {\\r\\nset req.http.X-Forwarded-For = client.ip;\\r\\n}\\r\\n}\\r\\n# httpoxy\\r\\nunset req.http.proxy;\\r\\n# Non-RFC2616 or CONNECT which is weird.\\r\\nif (\\r\\nreq.method != \\\"GET\\\" \\u0026\\u0026\\r\\nreq.method != \\\"HEAD\\\" \\u0026\\u0026\\r\\nreq.method != \\\"PUT\\\" \\u0026\\u0026\\r\\nreq.method != \\\"POST\\\" \\u0026\\u0026\\r\\nreq.method != \\\"TRACE\\\" \\u0026\\u0026\\r\\nreq.method != \\\"OPTIONS\\\" \\u0026\\u0026\\r\\nreq.method != \\\"DELETE\\\"\\r\\n) {\\r\\nreturn (pipe);\\r\\n}\\r\\n# We only deal with GET and HEAD by default\\r\\nif (req.method != \\\"GET\\\" \\u0026\\u0026 req.method != \\\"HEAD\\\") {\\r\\nreturn (pass);\\r\\n}\\r\\n# === URL manipulation ===\\r\\n# First remove the Google Analytics added parameters, useless for our backend\\r\\nif (req.url ~ \\\"(?|\\u0026)(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=\\\") {\\r\\nset req.url = regsuball(req.url, \\\"\\u0026(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=([A-z0-9_-.%25]+)\\\", \\\"\\\");\\r\\nset req.url = regsuball(req.url, \\\"?(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=([A-z0-9_-.%25]+)\\\", \\\"?\\\");\\r\\nset req.url = regsub(req.url, \\\"?\\u0026\\\", \\\"?\\\");\\r\\nset req.url = regsub(req.url, \\\"?$\\\", \\\"\\\");\\r\\n}\\r\\n# Strip hash, server doesn\'t need it.\\r\\nif (req.url ~ \\\"#\\\") {\\r\\nset req.url = regsub(req.url, \\\"#.*$\\\", \\\"\\\");\\r\\n}\\r\\n# Strip a trailing ? if it exists\\r\\n#if (req.url ~ \\\"?$\\\") {\\r\\n#    set req.url = regsub(req.url, \\\"?$\\\", \\\"\\\");\\r\\n#}\\r\\n# === Generic cookie manipulation ===\\r\\n# Remove the \\\"has_js\\\" cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"has_js=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove any Google Analytics based cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__utm.=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"_ga=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"_gat=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmctr=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmcmd.=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmccn.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove DoubleClick offensive cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__gads=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the Quant Capital cookies (added by some plugin, all __qca)\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__qc.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the AddThis cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__atuv.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the wp-settings-1 cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"wp-settings-1=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the wp-settings-time-1 cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"wp-settings-time-1=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the wp test cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"wordpress_test_cookie=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove a \\\";\\\" prefix in the cookie if present\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"^;s*\\\", \\\"\\\");\\r\\n# Are there cookies left with only spaces or that are empty?\\r\\nif (req.http.cookie ~ \\\"^s*$\\\") {\\r\\nunset req.http.cookie;\\r\\n}\\r\\n# Check for the custom \\\"X-Logged-In\\\" header (used by K2 and other apps) to identify\\r\\n# if the visitor is a guest, then unset any cookie (including session cookies) provided\\r\\n# it\'s not a POST request.\\r\\nif(req.http.X-Logged-In == \\\"False\\\" \\u0026\\u0026 req.method != \\\"POST\\\") {\\r\\nunset req.http.Cookie;\\r\\n}\\r\\n# === DO NOT CACHE ===\\r\\n# Don\'t cache HTTP authorization/authentication pages and pages with certain headers or cookies\\r\\nif (\\r\\nreq.http.Authorization ||\\r\\nreq.http.Authenticate ||\\r\\nreq.http.X-Logged-In == \\\"True\\\" ||\\r\\nreq.http.Cookie ~ \\\"userID\\\" ||\\r\\nreq.http.Cookie ~ \\\"joomla_[a-zA-Z0-9_]+\\\" ||\\r\\nreq.http.Cookie ~ \\\"(wordpress_[a-zA-Z0-9_]+|wp-postpass|comment_author_[a-zA-Z0-9_]+|woocommerce_cart_hash|woocommerce_items_in_cart|wp_woocommerce_session_[a-zA-Z0-9]+)\\\"\\r\\n) {\\r\\n#set req.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set req.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set req.http.Pragma = \\\"no-cache\\\";\\r\\nreturn (pass);\\r\\n}\\r\\n# Exclude the following paths (e.g. backend admins, user pages or ad URLs that require tracking)\\r\\n# In Joomla specifically, you are advised to create specific entry points (URLs) for users to\\r\\n# interact with the site (either common user logins or even commenting), e.g. make a menu item\\r\\n# to point to a user login page (e.g. /login), including all related functionality such as\\r\\n# password reset, email reminder and so on.\\r\\nif(\\r\\nreq.url ~ \\\"^/addons\\\" ||\\r\\nreq.url ~ \\\"^/administrator\\\" ||\\r\\nreq.url ~ \\\"^/cart\\\" ||\\r\\nreq.url ~ \\\"^/checkout\\\" ||\\r\\nreq.url ~ \\\"^/component/banners\\\" ||\\r\\nreq.url ~ \\\"^/component/socialconnect\\\" ||\\r\\nreq.url ~ \\\"^/component/users\\\" ||\\r\\nreq.url ~ \\\"^/connect\\\" ||\\r\\nreq.url ~ \\\"^/contact\\\" ||\\r\\nreq.url ~ \\\"^/login\\\" ||\\r\\nreq.url ~ \\\"^/logout\\\" ||\\r\\nreq.url ~ \\\"^/lost-password\\\" ||\\r\\nreq.url ~ \\\"^/my-account\\\" ||\\r\\nreq.url ~ \\\"^/register\\\" ||\\r\\nreq.url ~ \\\"^/signin\\\" ||\\r\\nreq.url ~ \\\"^/signup\\\" ||\\r\\nreq.url ~ \\\"^/wc-api\\\" ||\\r\\nreq.url ~ \\\"^/wp-admin\\\" ||\\r\\nreq.url ~ \\\"^/wp-login.php\\\" ||\\r\\nreq.url ~ \\\"^?add-to-cart=\\\" ||\\r\\nreq.url ~ \\\"^?wc-api=\\\"\\r\\n) {\\r\\n#set req.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set req.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set req.http.Pragma = \\\"no-cache\\\";\\r\\nreturn (pass);\\r\\n}\\r\\n# Don\'t cache ajax requests\\r\\nif(req.http.X-Requested-With == \\\"XMLHttpRequest\\\" || req.url ~ \\\"nocache\\\") {\\r\\n#set req.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set req.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set req.http.Pragma = \\\"no-cache\\\";\\r\\nreturn (pass);\\r\\n}\\r\\n# === STATIC FILES ===\\r\\n# Properly handle different encoding types\\r\\nif (req.http.Accept-Encoding) {\\r\\nif (req.url ~ \\\".(jpg|jpeg|png|gif|gz|tgz|bz2|tbz|mp3|ogg|swf)$\\\") {\\r\\n# No point in compressing these\\r\\nunset req.http.Accept-Encoding;\\r\\n} elseif (req.http.Accept-Encoding ~ \\\"gzip\\\") {\\r\\nset req.http.Accept-Encoding = \\\"gzip\\\";\\r\\n} elseif (req.http.Accept-Encoding ~ \\\"deflate\\\") {\\r\\nset req.http.Accept-Encoding = \\\"deflate\\\";\\r\\n} else {\\r\\n# unknown algorithm (aka crappy browser)\\r\\nunset req.http.Accept-Encoding;\\r\\n}\\r\\n}\\r\\n# Remove all cookies for static files \\u0026 deliver directly\\r\\nif (req.url ~ \\\"^[^?]*.(7z|avi|bmp|bz2|css|csv|doc|docx|eot|flac|flv|gif|gz|ico|jpeg|jpg|js|less|mka|mkv|mov|mp3|mp4|mpeg|mpg|odt|ogg|ogm|opus|otf|pdf|png|ppt|pptx|rar|rtf|svg|svgz|swf|tar|tbz|tgz|ttf|txt|txz|wav|webm|webp|woff|woff2|xls|xlsx|xml|xz|zip)(?.*)?$\\\") {\\r\\nunset req.http.Cookie;\\r\\nreturn (hash);\\r\\n}\\r\\nreturn (hash);\\r\\n}\\r\\nsub vcl_backend_response {\\r\\n/*\\r\\n# If we host multiple domains on a server, here you can list the domains you DO NOT want to cache\\r\\n# The first check matches both naked \\u0026 \\\"www\\\" subdomains. Use the second for non generic subdomains.\\r\\nif (\\r\\nbereq.http.host ~ \\\"(www.)?(domain1.com|domain2.org|domain3.net)\\\" ||\\r\\nbereq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\"\\r\\n) {\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n*/\\r\\n# Don\'t cache 50x responses\\r\\nif (\\r\\nberesp.status == 500 ||\\r\\nberesp.status == 502 ||\\r\\nberesp.status == 503 ||\\r\\nberesp.status == 504\\r\\n) {\\r\\nreturn (abandon);\\r\\n}\\r\\n# === DO NOT CACHE ===\\r\\n# Exclude the following paths (e.g. backend admins, user pages or ad URLs that require tracking)\\r\\n# In Joomla specifically, you are advised to create specific entry points (URLs) for users to\\r\\n# interact with the site (either common user logins or even commenting), e.g. make a menu item\\r\\n# to point to a user login page (e.g. /login), including all related functionality such as\\r\\n# password reset, email reminder and so on.\\r\\nif(\\r\\nbereq.url ~ \\\"^/addons\\\" ||\\r\\nbereq.url ~ \\\"^/administrator\\\" ||\\r\\nbereq.url ~ \\\"^/cart\\\" ||\\r\\nbereq.url ~ \\\"^/checkout\\\" ||\\r\\nbereq.url ~ \\\"^/component/banners\\\" ||\\r\\nbereq.url ~ \\\"^/component/socialconnect\\\" ||\\r\\nbereq.url ~ \\\"^/component/users\\\" ||\\r\\nbereq.url ~ \\\"^/connect\\\" ||\\r\\nbereq.url ~ \\\"^/contact\\\" ||\\r\\nbereq.url ~ \\\"^/login\\\" ||\\r\\nbereq.url ~ \\\"^/logout\\\" ||\\r\\nbereq.url ~ \\\"^/lost-password\\\" ||\\r\\nbereq.url ~ \\\"^/my-account\\\" ||\\r\\nbereq.url ~ \\\"^/register\\\" ||\\r\\nbereq.url ~ \\\"^/signin\\\" ||\\r\\nbereq.url ~ \\\"^/signup\\\" ||\\r\\nbereq.url ~ \\\"^/wc-api\\\" ||\\r\\nbereq.url ~ \\\"^/wp-admin\\\" ||\\r\\nbereq.url ~ \\\"^/wp-login.php\\\" ||\\r\\nbereq.url ~ \\\"^?add-to-cart=\\\" ||\\r\\nbereq.url ~ \\\"^?wc-api=\\\"\\r\\n) {\\r\\n#set beresp.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set beresp.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set beresp.http.Pragma = \\\"no-cache\\\";\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Don\'t cache HTTP authorization/authentication pages and pages with certain headers or cookies\\r\\nif (\\r\\nbereq.http.Authorization ||\\r\\nbereq.http.Authenticate ||\\r\\nbereq.http.X-Logged-In == \\\"True\\\" ||\\r\\nbereq.http.Cookie ~ \\\"userID\\\" ||\\r\\nbereq.http.Cookie ~ \\\"joomla_[a-zA-Z0-9_]+\\\" ||\\r\\nbereq.http.Cookie ~ \\\"(wordpress_[a-zA-Z0-9_]+|wp-postpass|comment_author_[a-zA-Z0-9_]+|woocommerce_cart_hash|woocommerce_items_in_cart|wp_woocommerce_session_[a-zA-Z0-9]+)\\\"\\r\\n) {\\r\\n#set beresp.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set beresp.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set beresp.http.Pragma = \\\"no-cache\\\";\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Don\'t cache ajax requests\\r\\nif(beresp.http.X-Requested-With == \\\"XMLHttpRequest\\\" || bereq.url ~ \\\"nocache\\\") {\\r\\n#set beresp.http.Cache-Control = \\\"private, max-age=0, no-cache, no-store\\\";\\r\\n#set beresp.http.Expires = \\\"Mon, 01 Jan 2001 00:00:00 GMT\\\";\\r\\n#set beresp.http.Pragma = \\\"no-cache\\\";\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Don\'t cache backend response to posted requests\\r\\nif (bereq.method == \\\"POST\\\") {\\r\\nset beresp.uncacheable = true;\\r\\nreturn (deliver);\\r\\n}\\r\\n# Ok, we\'re cool \\u0026 ready to cache things\\r\\n# so let\'s clean up some headers and cookies\\r\\n# to maximize caching.\\r\\n# Check for the custom \\\"X-Logged-In\\\" header to identify if the visitor is a guest,\\r\\n# then unset any cookie (including session cookies) provided it\'s not a POST request.\\r\\nif(beresp.http.X-Logged-In == \\\"False\\\" \\u0026\\u0026 bereq.method != \\\"POST\\\") {\\r\\nunset beresp.http.Set-Cookie;\\r\\n}\\r\\n# Unset the \\\"pragma\\\" header (suggested)\\r\\nunset beresp.http.Pragma;\\r\\n# Unset the \\\"vary\\\" header (suggested)\\r\\nunset beresp.http.Vary;\\r\\n# Unset the \\\"etag\\\" header (optional)\\r\\n#unset beresp.http.etag;\\r\\n# Allow stale content, in case the backend goes down\\r\\nset beresp.grace = 24h;\\r\\n# Enforce your own cache TTL (optional)\\r\\n#set beresp.ttl = 180s;\\r\\n# Modify \\\"expires\\\" header - https://www.varnish-cache.org/trac/wiki/VCLExampleSetExpires (optional)\\r\\n#set beresp.http.Expires = \\\"\\\" + (now + beresp.ttl);\\r\\n# If your backend server does not set the right caching headers for static assets,\\r\\n# you can set them below (uncomment first and change 604800 - which 1 week - to whatever you\\r\\n# want (in seconds)\\r\\n#if (bereq.url ~ \\\".(ico|jpg|jpeg|gif|png|bmp|webp|tiff|svg|svgz|pdf|mp3|flac|ogg|mid|midi|wav|mp4|webm|mkv|ogv|wmv|eot|otf|woff|ttf|rss|atom|zip|7z|tgz|gz|rar|bz2|tar|exe|doc|docx|xls|xlsx|ppt|pptx|rtf|odt|ods|odp)(?[a-zA-Z0-9=]+)$\\\") {\\r\\n#    set beresp.http.Cache-Control = \\\"public, max-age=604800\\\";\\r\\n#}\\r\\nif (bereq.url ~ \\\"^[^?]*.(7z|avi|bmp|bz2|css|csv|doc|docx|eot|flac|flv|gif|gz|ico|jpeg|jpg|js|less|mka|mkv|mov|mp3|mp4|mpeg|mpg|odt|ogg|ogm|opus|otf|pdf|png|ppt|pptx|rar|rtf|svg|svgz|swf|tar|tbz|tgz|ttf|txt|txz|wav|webm|webp|woff|woff2|xls|xlsx|xml|xz|zip)(?.*)?$\\\") {\\r\\nunset beresp.http.set-cookie;\\r\\nset beresp.do_stream = true;\\r\\n}\\r\\n# We have content to cache, but it\'s got no-cache or other Cache-Control values sent\\r\\n# So let\'s reset it to our main caching time (180s as used in this example configuration)\\r\\n# The additional parameters specified (stale-while-revalidate \\u0026 stale-if-error) are used\\r\\n# by modern browsers to better control caching. Set these to twice \\u0026 four times your main\\r\\n# cache time respectively.\\r\\n# This final setting will normalize cache-control headers for CMSs like Joomla\\r\\n# which set max-age=0 even when the CMS\' cache is enabled.\\r\\nif (beresp.http.Cache-Control !~ \\\"max-age\\\" || beresp.http.Cache-Control ~ \\\"max-age=0\\\") {\\r\\nset beresp.http.Cache-Control = \\\"public, max-age=180, stale-while-revalidate=360, stale-if-error=43200\\\";\\r\\n}\\r\\n# Optionally set a larger TTL for pages with less than 180s of cache TTL\\r\\n#if (beresp.ttl \\u003c 180s) {\\r\\n#    set beresp.http.Cache-Control = \\\"public, max-age=180, stale-while-revalidate=360, stale-if-error=43200\\\";\\r\\n#}\\r\\nreturn (deliver);\\r\\n}\\r\\nsub vcl_deliver {\\r\\n/*\\r\\n# Send a special header for excluded domains only\\r\\n# The if statement can be identical to the ones in the vcl_recv() and vcl_fetch() functions above\\r\\nif (\\r\\nreq.http.host ~ \\\"(www.)?(domain1.com|domain2.org|domain3.net)\\\" ||\\r\\nreq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\"\\r\\n) {\\r\\nset resp.http.X-Domain-Status = \\\"EXCLUDED\\\";\\r\\n}\\r\\n# Enforce redirect to HTTPS for specified domains only\\r\\nif (\\r\\nreq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\" \\u0026\\u0026\\r\\nreq.http.X-Forwarded-Proto !~ \\\"(?i)https\\\"\\r\\n) {\\r\\nset resp.http.Location = \\\"https://\\\" + req.http.host + req.url;\\r\\nset resp.status = 302;\\r\\n}\\r\\n*/\\r\\n# Send special headers that indicate the cache status of each web page\\r\\nif (obj.hits \\u003e 0) {\\r\\nset resp.http.X-Cache = \\\"HIT\\\";\\r\\nset resp.http.X-Cache-Hits = obj.hits;\\r\\n} else {\\r\\nset resp.http.X-Cache = \\\"MISS\\\";\\r\\n}\\r\\nreturn (deliver);\\r\\n}\\r\\nNginx Host 配置\\r\\nserver {\\r\\nlisten 80;\\r\\nserver_name www.phpman.cc phpman.cc;\\r\\nrewrite ^/(.*) https://www.phpman.cc/$1 permanent;\\r\\n}\\r\\nserver\\r\\n{\\r\\nlisten 8080;\\r\\nindex index.html index.htm index.php default.html default.htm default.php;\\r\\nroot /home/wwwroot/www.phpman.cc/wordpress;\\r\\ninclude rewrite/wordpress.conf;\\r\\n#error_page   404   /404.html;\\r\\n# Deny access to PHP files in specific directory\\r\\n#location ~ /(wp-content|uploads|wp-includes|images)/.*.php$ { deny all; }\\r\\ninclude enable-php-pathinfo.conf;\\r\\nlocation ~ .*.(gif|jpg|jpeg|png|bmp|swf)$\\r\\n{\\r\\nexpires      30d;\\r\\n}\\r\\nlocation ~ .*.(js|css)?$\\r\\n{\\r\\nexpires      12h;\\r\\n}\\r\\nlocation ~ /.well-known {\\r\\nallow all;\\r\\n}\\r\\nlocation ~ /.\\r\\n{\\r\\ndeny all;\\r\\n}\\r\\naccess_log  /home/wwwlogs/y.log;\\r\\n}\\r\\nserver\\r\\n{\\r\\nlisten 443 ssl http2;\\r\\n#listen [::]:443 ssl http2;\\r\\nserver_name www.phpman.cc phpman.cc;\\r\\nssl_certificate /root/ssl/4082867_phpman.cc.pem;\\r\\nssl_certificate_key /root/ssl/4082867_phpman.cc.key;\\r\\nssl_session_timeout 5m;\\r\\nssl_protocols TLSv1 TLSv1.1 TLSv1.2 TLSv1.3;\\r\\nssl_prefer_server_ciphers on;\\r\\nssl_ciphers \\\"TLS13-AES-256-GCM-SHA384:TLS13-CHACHA20-POLY1305-SHA256:TLS13-AES-128-GCM-SHA256:TLS13-AES-128-CCM-8-SHA256:TLS13-AES-128-CCM-SHA256:EECDH+CHACHA20:EECDH+CHACHA20-draft:EECDH+AES128:RSA+AES128:EECDH+AES256:RSA+AES256:EECDH+3DES:RSA+3DES:!MD5\\\";\\r\\nssl_session_cache builtin:1000 shared:SSL:10m;\\r\\n# openssl dhparam -out /usr/local/nginx/conf/ssl/dhparam.pem 2048\\r\\nssl_dhparam /usr/local/nginx/conf/ssl/dhparam.pem;\\r\\nlocation / {\\r\\nproxy_pass http://127.0.0.1:6081;\\r\\nproxy_set_header X-Real-IP  $remote_addr;\\r\\nproxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;\\r\\nproxy_set_header X-Forwarded-Proto https;\\r\\nproxy_set_header X-Forwarded-Port 443;\\r\\nproxy_set_header Host $host;\\r\\n}\\r\\n}\\r\\nWordPress wp-config.php配置\\r\\n/* SSL Settings */\\r\\ndefine(\'FORCE_SSL_ADMIN\', true);\\r\\n/* Turn HTTPS \'on\' if HTTP_X_FORWARDED_PROTO matches \'https\' */\\r\\nif (strpos($_SERVER[\'HTTP_X_FORWARDED_PROTO\'], \'https\') !== false) {\\r\\n$_SERVER[\'HTTPS\'] = \'on\';\\r\\n}\\r\\n重启Nginx 和 Varnish 服务\\r\\nservice nginx reload\\r\\nservice varnish reload\\r\\n总结\\r\\nWeb URL请求路径：\\r\\nNginx:80——\\u003eNginx:443 ——\\u003e Varnish:6081 ——\\u003e Nginx:8080\\r\\n验证Varnish是否生效\\r\\ncurl   -I https://www.phpman.cc\\r\\n返回结果\\r\\nHTTP/1.1 200 OK\\r\\nServer: nginx\\r\\nDate: Tue, 13 Apr 2021 06:41:21 GMT\\r\\nContent-Type: text/html; charset=UTF-8\\r\\nConnection: keep-alive\\r\\nVary: Accept-Encoding\\r\\nVary: Accept-Encoding\\r\\nX-Powered-By: PHP/7.4.15\\r\\nLink: ; rel=\\\"https://api.w.org/\\\"\\r\\nX-Varnish: 360465\\r\\nAge: 0\\r\\nVia: 1.1 varnish-v4\\r\\n\"],\"id\":[\"16\"],\"title\":[\"Varnish 加速你的WordPress\"],\"updated_at\":[\"\"]}','2021-09-12 14:43:57','2021-09-12 14:43:57'),(441,1,'/admin/edit/posts','POST','::1','{\"__go_admin_previous_\":[\"/admin/info/posts?__page=1\\u0026__pageSize=10\\u0026__sort=id\\u0026__sort_type=desc\"],\"__go_admin_t_\":[\"8c91a1fd-2b1a-4c86-89bc-2e01c73e8db4\"],\"content\":[\"\\r\\nVarnish Cache是​​Web应用程序加速器，也称为缓存HTTP反向代理。您可以将其安装在任何使用HTTP的服务器之前，并将其配置为缓存内容。Varnish Cache确实非常快。根据您的体系结构，通常可以将交付速度提高300到1000倍。\\r\\nCentOs 安装\\r\\nyum install varnish\\r\\nVarnish 配置\\r\\n###############################################################################################\\r\\n### The perfect Varnish 4.x+ configuration for Joomla, WordPress \\u0026 other CMS based websites ###\\r\\n###############################################################################################\\r\\n######################\\r\\n#\\r\\n# UPDATED on July 7th, 2020\\r\\n#\\r\\n# Configuration Notes:\\r\\n# 1. Default dynamic content caching respects your backend\'s cache-control HTTP header.\\r\\n#    If however you need to enforce a different cache-control TTL,\\r\\n#    do a search for \\\"180\\\" and replace with the new value in seconds.\\r\\n#    Stale cache is served for up to 24 hours.\\r\\n# 2. Make sure you update the \\\"backend default { ... }\\\" section with the correct IP and port\\r\\n#\\r\\n######################\\r\\n# Varnish Reference:\\r\\n# See the VCL chapters in the Users Guide at https://www.varnish-cache.org/docs/\\r\\n# and https://www.varnish-cache.org/trac/wiki/VCLExamples for more examples.\\r\\n# Marker to tell the VCL compiler that this VCL has been adapted to the new 4.0 format\\r\\nvcl 4.0;\\r\\n# Imports\\r\\nimport std;\\r\\n# Default backend definition. Set this to point to your content server.\\r\\nbackend default {\\r\\n.host = \\\"127.0.0.1\\\"; # UPDATE this only if the web server is not on the same machine\\r\\n.port = \\\"8080\\\";      # UPDATE 8080 with your web server\'s (internal) port\\r\\n}\\r\\nsub vcl_recv {\\r\\n/*\\r\\n# Blocks\\r\\nif (req.http.user-agent ~ \\\"^$\\\" \\u0026\\u0026 req.http.referer ~ \\\"^$\\\") {\\r\\nreturn (synth(204, \\\"No content\\\"));\\r\\n}\\r\\nif (req.http.user-agent ~ \\\"(ahrefs|bingbot|domaincrawler|dotbot|mj12bot|semrush)\\\") {\\r\\nreturn (synth(204, \\\"Bot blocked\\\"));\\r\\n}\\r\\n# If we host multiple domains on a server, here you can list the domains you DO NOT want to cache\\r\\n# The first check matches both naked \\u0026 \\\"www\\\" subdomains. Use the second for non generic subdomains.\\r\\nif (\\r\\nreq.http.host ~ \\\"(www.)?(domain1.com|domain2.org|domain3.net)\\\" ||\\r\\nreq.http.host ~ \\\"(subdomain.domain4.tld|othersubdomain.domain5.tld)\\\"\\r\\n) {\\r\\nreturn (pass);\\r\\n}\\r\\n*/\\r\\n# LetsEncrypt Certbot passthrough\\r\\nif (req.url ~ \\\"^/.well-known/acme-challenge/\\\") {\\r\\nreturn (pass);\\r\\n}\\r\\n# Forward client\'s IP to the backend\\r\\nif (req.restarts == 0) {\\r\\nif (req.http.X-Real-IP) {\\r\\nset req.http.X-Forwarded-For = req.http.X-Real-IP;\\r\\n} else if (req.http.X-Forwarded-For) {\\r\\nset req.http.X-Forwarded-For = req.http.X-Forwarded-For + \\\", \\\" + client.ip;\\r\\n} else {\\r\\nset req.http.X-Forwarded-For = client.ip;\\r\\n}\\r\\n}\\r\\n# httpoxy\\r\\nunset req.http.proxy;\\r\\n# Non-RFC2616 or CONNECT which is weird.\\r\\nif (\\r\\nreq.method != \\\"GET\\\" \\u0026\\u0026\\r\\nreq.method != \\\"HEAD\\\" \\u0026\\u0026\\r\\nreq.method != \\\"PUT\\\" \\u0026\\u0026\\r\\nreq.method != \\\"POST\\\" \\u0026\\u0026\\r\\nreq.method != \\\"TRACE\\\" \\u0026\\u0026\\r\\nreq.method != \\\"OPTIONS\\\" \\u0026\\u0026\\r\\nreq.method != \\\"DELETE\\\"\\r\\n) {\\r\\nreturn (pipe);\\r\\n}\\r\\n# We only deal with GET and HEAD by default\\r\\nif (req.method != \\\"GET\\\" \\u0026\\u0026 req.method != \\\"HEAD\\\") {\\r\\nreturn (pass);\\r\\n}\\r\\n# === URL manipulation ===\\r\\n# First remove the Google Analytics added parameters, useless for our backend\\r\\nif (req.url ~ \\\"(?|\\u0026)(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=\\\") {\\r\\nset req.url = regsuball(req.url, \\\"\\u0026(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=([A-z0-9_-.%25]+)\\\", \\\"\\\");\\r\\nset req.url = regsuball(req.url, \\\"?(utm_source|utm_medium|utm_campaign|utm_content|gclid|cx|ie|cof|siteurl)=([A-z0-9_-.%25]+)\\\", \\\"?\\\");\\r\\nset req.url = regsub(req.url, \\\"?\\u0026\\\", \\\"?\\\");\\r\\nset req.url = regsub(req.url, \\\"?$\\\", \\\"\\\");\\r\\n}\\r\\n# Strip hash, server doesn\'t need it.\\r\\nif (req.url ~ \\\"#\\\") {\\r\\nset req.url = regsub(req.url, \\\"#.*$\\\", \\\"\\\");\\r\\n}\\r\\n# Strip a trailing ? if it exists\\r\\n#if (req.url ~ \\\"?$\\\") {\\r\\n#    set req.url = regsub(req.url, \\\"?$\\\", \\\"\\\");\\r\\n#}\\r\\n# === Generic cookie manipulation ===\\r\\n# Remove the \\\"has_js\\\" cookie\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"has_js=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove any Google Analytics based cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__utm.=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"_ga=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"_gat=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmctr=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmcmd.=[^;]+(; )?\\\", \\\"\\\");\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"utmccn.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove DoubleClick offensive cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__gads=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the Quant Capital cookies (added by some plugin, all __qca)\\r\\nset req.http.Cookie = regsuball(req.http.Cookie, \\\"__qc.=[^;]+(; )?\\\", \\\"\\\");\\r\\n# Remove the AddThis cookies\\r\\nset req.http.Cookie = regsuball(req.http.Cookie